import React from "react";
import Style from "./Zikr.module.css";
import { useState } from "react";
const Zikr = () => {
  const [count, setCount] = useState(0);

  function plus() {
    setCount(count + 1);
  }
  return (
    <div className={Style.header}>
      <h2>الذكر</h2>
      <h1></h1>
      <div className={Style.buttons}>
        <div className={Style.button_child}>
          <button onClick={plus}>Субханаллах</button>
          <h2>{count}</h2>
        </div>
        <div className={Style.button_child}>
          <button>Аль-ха́мду ли-Лля́х</button>
          <h2></h2>
        </div>
        <div>
          <button className={Style.button_child}>Алла́ху А́кбар</button>
          <h2></h2>
        </div>
      </div>
    </div>
  );
};

export default Zikr;
