import { useState } from 'react';



function App() {
  let [count, setCount] = useState(0)

  function plus(){
    setCount(count + 1)
  }
  function restart(){
    setCount(count = 0)
  }


  return (
    <div className="App">
        <h1>الذكر</h1> <span>Зикр</span>

        <h3>{count}</h3>

        <button onClick={plus}>+</button>
        <button onClick={restart}>restart</button>

    </div>
  );
}

export default App;
