import { useState } from 'react';

const zikir = ['sub','alh','alk']

function App() {
  let [count, setCount] = useState(0)

  function plus(){
    setCount(count + 1)
  }
  function restart(){
    setCount(count = 0)
  }


  return (
    <div className="App">
        <h1>Al zikir</h1>
        {zikir.map((item)=>{
          return (
            <div>
              <p>{item[0]}</p>
            </div>
          )
        })

        }
        <h3>{count}</h3>

        <button onClick={plus}>+</button>
        <button onClick={restart}>restart</button>

    </div>
  );
}

export default App;
